import React, {Component} from "react"
import {Redirect, Link} from "react-router-dom"
import axios from "axios"



export default class Login extends Component
{
    constructor(props)
    {
        super(props)

        this.state = {
            email:"",
            password:"",
            isLoggedIn:false
        }
    }




    handleChange = (e) => 
    {
        this.setState({[e.target.name]: e.target.value})
    }



    handleSubmit = (e) => 
    {
        e.preventDefault();

        const User = {
            email: this.state.email,
            password:this.state.password
        }

        this.setState({
            name: '',
            email: '',
            password:''
        });

        axios.post('http://localhost:4000/users/login', User)
        .then(res => 
            {     
                if(res.data)
                {
                    if (res.data.errorMessage)
                    {
                        console.log(res.data.errorMessage)    
                    }
                    else // user successfully registered
                    { 
                        console.log("User registered and logged in")

                        localStorage.name = res.data.name
                        localStorage.accessLevel = res.data.accessLevel                    
                        localStorage.token = res.data.tokens.access_token

                        this.setState({isRegistered:true})
                    }        
                }
                else
                {
                    console.log("Login failed")
                }
            })   
        }




    render()
    {            
        return (

            <div class="container">
            <form>
                <h2>Login</h2>
                <div class="mb-3">
                <label for="InputEmail" class="form-label">Email address</label>
                <input class="form-control" aria-describedby="emailHelp"
                    type = "email" 
                    name = "email" 
                    placeholder = "Email"
                    autoComplete="email"
                    value={this.state.email} 
                    onChange={this.handleChange}
                />
                 <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
                 </div>
                <input 
                    type = "password" 
                    name = "password" 
                    placeholder = "Password"
                    autoComplete="password"
                    value={this.state.password} 
                    onChange={this.handleChange}
                /><br/><br/>

                <button value="Login" className="green-button" onClick={this.handleSubmit}>Login</button>                                       
            </form>
            </div>
        )
    }
}
